import math

def isSquare(x):
    # Check http://burningmath.blogspot.be/2013/09/how-to-check-if-number-is-perfect-square.html 
    # for the criteria that led to this code.
    # The idea is that checking whether a number is a perfect square is an expensive operation
    # and should only be done when basic criteria indicate that the number could be a perfect square.
    # Note that the term "digital root" is identical to the remainder when dividing by 9
    if x == 1:
        return True
    if x%9 in [2,3,5,6,8]:
        return False
    finalDigit = x%10
    if finalDigit in [2,3,7,8]:
        return False
    final2Digits = x%100
    final2Digit = final2Digits//10
    if finalDigit == 6:
        if final2Digit in [0,2,4,6,8]:
            return False
    else:
        if final2Digit in [1,3,5,7,9]:
            return False
    if finalDigit == 5:
        if final2Digit != 2:
            return False
    if finalDigit == 0:
        if final2Digit != 0:
            return False    
    if finalDigit in [0,2,4,6,8]:
        if final2Digits%4 != 0:
            final3Digits = x%1000
            if final3Digits%8 != 4:
                return False
    if finalDigit in [1,3,5,7,9]:
        final3Digits = x%1000
        if final3Digits%8 != 1:
            return False    
    low = 0
    high = x // 2
    root = high
    while root * root != x:
        root = (low + high) // 2
        if low + 1 >= high:
            return False
        if root * root > x:
            high = root
        else:
            low = root
    return True

def highSquareRoot(x):
    # returns the integer higher than the square root
    # this function should only be used for x=p*q
    # so, when we are certain that x is not a perfect square
    if x == 1:
        return True
    low = 0
    high = x // 2
    root = high
    while root * root != x:
        root = (low + high) // 2
        if low + 1 >= high:
            return low+1
        if root * root > x:
            high = root
        else:
            low = root
    return low+1

def lowSquareRoot(x):
    # returns the integer lower than or equal to the square root
    if x == 1:
        return True
    low = 0
    high = x // 2
    root = high
    while root * root != x:
        root = (low + high) // 2
        if low + 1 >= high:
            return low
        if root * root > x:
            high = root
        else:
            low = root
    return low

def factor(n):
    x = highSquareRoot(n)
    max = 2*x
    i = 0
    bl = 0
    while x<max:
        i = i + 1
        nbl = i.bit_length()
        if nbl != bl:
            bl = nbl
            basis = pow(2,bl)
            step = basis//10
            if step < 1:
                step = 1
        if i%step == 0:
            print ("trial ", i," x = ", x)
        sq = x**2 - n
        # sqr = int(math.sqrt(sq)+0.5)
        # sqr = lowSquareRoot(sq)
        # if sqr**2 = sq:
        if isSquare(sq):
            y = lowSquareRoot(sq)
            q = x + y
            p = x - y
            return p,q
        else:
            x = x + 1
    return 0,0

